mapper.update:
update users
set username=#{username},
    password=#{password},
    account=#{account}
where id = #{id}

mapper.select:
select id, username, password, account, items as jsItems, state
from users
where id = #{id}

select id, username, password, account, items as jsItems, state
from users
where username = #{username}

select id, username, password, account, items as jsItems, state
from users
where account = #{account}

mapper.update:
update users
set password = #{password}
where id = #{id}

mapper.select:
select id, username, password, account, items, state, create_time, update_time
from users
where username like #{username}
order by id
limit #{cur},#{pageSize};
